def update_beta(phi: list[np.ndarray], corpus: list[list[int]], V: int) -> list[list[float]]:
    """Update the word–topic distribution β in Latent Dirichlet Allocation."""
    beta = np.zeros((V, len(phi[0])))
    for (d, phi_d) in enumerate(phi):
        for (n, w) in enumerate(corpus[d]):
            beta[w, d] += phi_d[n].sum()
    for t in range(len(phi[0])):
        if beta[:, t].sum() > 0:
            beta[:, t] /= beta[:, t].sum()
    beta_rounded = [list(round(x, 4)) for x in beta]
    return beta_rounded

