def to_networkx(G: Dict[str, Any]) -> Dict[Any, List[Tuple[Any, float]]]:
    """Convert a custom graph dictionary into a plain adjacency dictionary."""
    is_directed = G['is_directed']
    V2I = G['_V2I']
    G_edges = G['_G']
    adj = {label: [] for label in V2I.keys()}
    for (source_index, targets) in enumerate(G_edges):
        source_label = list(V2I.keys())[source_index]
        for (target_label, weight) in targets:
            target_index = V2I[target_label]
            edge = (target_label, weight)
            adj[source_label].append(edge)
            if not is_directed:
                adj[target_label].append((source_label, weight))
    for (label, neighbors) in adj.items():
        adj[label] = sorted(neighbors, key=lambda x: x[0])
    return adj

import math
def expected_log_dirichlet(gamma, d, t):
    """Compute the expectation of log X_t for a Dirichlet-distributed vector."""

    def digamma(x):
        while x < 6:
            x += 1.0 / x
        if x >= 6:
            return math.log(x) - 1.0 / (2 * x) - 1.0 / (12 * x ** 2) + 1.0 / (120 * x ** 4) - 1.0 / (252 * x ** 6)
        else:
            raise ValueError('This implementation only handles x >= 6.')
    gamma_sum = sum(gamma[d])
    log_expectation = digamma(gamma[d][t]) - digamma(gamma_sum)
    return round(log_expectation, 4)

