def kmeans(data: np.ndarray, K: int, max_iters: int=100) -> list[list[float]]:
    """Cluster *data* into *K* groups using the K–Means algorithm."""
    centroids = data[:K].copy()
    for _ in range(max_iters):
        assignments = np.array([np.argmin([euclidean_distance(sample, centroid) for centroid in centroids]) for sample in data])
        new_centroids = [data[assignments == k].mean(axis=0) for k in range(K)]
        if all((np.allclose(old, new, atol=1e-06) for (old, new) in zip(centroids, new_centroids))):
            break
        centroids = np.array(new_centroids)
    sorted_centroids = [list(map(lambda x: round(x, 4), centroid)) for centroid in sorted(centroids.tolist())]
    return sorted_centroids

