def decision_stump(X: List[List[float]], y: List[int]) -> Tuple[int, Optional[float], int, int]:

    def entropy(labels: List[int]) -> float:
        hist = np.bincount(labels)
        ps = hist / len(labels)
        return -np.sum((p * np.log2(p) for p in ps if p != 0))
    (n_samples, n_features) = (len(X), len(X[0]))
    (best_feat, best_thr) = (-1, None)
    (max_gain, majority_y) = (entropy(y), max(y, key=y.count))
    for feat_idx in range(n_features):
        sorted_uniq = sorted(set([row[feat_idx] for row in X]))
        mid_points = [(v1 + v2) / 2.0 for (v1, v2) in zip(sorted_uniq[:-1], sorted_uniq[1:])]
        for thr in mid_points:
            mask = [x <= thr for x in X[:, feat_idx]]
            (left_labels, right_labels) = (y[mask], y[np.logical_not(mask)])
            if len(left_labels) == 0 or len(right_labels) == 0:
                continue
            current_gain = entropy(y) - (len(left_labels) / n_samples * entropy(left_labels) + len(right_labels) / n_samples * entropy(right_labels))
            if current_gain > max_gain or (current_gain == max_gain and feat_idx < best_feat) or (current_gain == max_gain and feat_idx == best_feat and (thr < best_thr)):
                (max_gain, best_feat, best_thr) = (current_gain, feat_idx, thr)
    if max_gain <= 0:
        return (-1, None, majority_y, majority_y)
    left_mask = [x <= best_thr for x in X[:, best_feat]]
    right_mask = [not m for m in left_mask]
    (left_pred, right_pred) = (max(y[left_mask], key=left_mask.count, default=majority_y), max(y[right_mask], key=right_mask.count, default=majority_y))
    return (best_feat, round(best_thr, 4), left_pred, right_pred)

import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """

    def best_split(X, y):
        min_sse = float('inf')
        best_index = None
        for i in range(len(X)):
            left_mask = X < X[i]
            right_mask = X >= X[i]
            left_y = np.array(y)[left_mask]
            right_y = np.array(y)[right_mask]
            ss_left = np.sum((left_y - np.mean(left_y)) ** 2) if len(left_y) > 1 else 0
            ss_right = np.sum((right_y - np.mean(right_y)) ** 2) if len(right_y) > 1 else 0
            sse = ss_left + ss_right
            if sse < min_sse:
                min_sse = sse
                best_index = i
        return best_index

    def build_tree(X, y, current_depth=0):
        if not X or len(np.unique(X)) == 1 or current_depth == max_depth or (len(y) < 2):
            return np.mean(y)
        split_index = best_split(X, y)
        if split_index is None:
            return np.mean(y)
        left_indices = [i for (i, x) in enumerate(X) if x < X[split_index]]
        right_indices = [i for (i, x) in enumerate(X) if x >= X[split_index]]
        left_branch = build_tree(np.array(X)[left_indices], np.array(y)[left_indices], current_depth + 1)
        right_branch = build_tree(np.array(X)[right_indices], np.array(y)[right_indices], current_depth + 1)
        return {'split': X[split_index], 'left': left_branch, 'right': right_branch}

    def predict_single(x, tree):
        if not isinstance(tree, dict):
            return tree
        if x < tree['split']:
            return predict_single(x, tree['left'])
        else:
            return predict_single(x, tree['right'])
    if not X_train or not X_test:
        return []
    tree = build_tree(X_train, y_train)
    predictions = [predict_single(x, tree) for x in X_test]
    return predictions

