import numpy as np
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    def scan_db(items_count):
        for transaction in transactions:
            for item in transaction:
                items_count[item] += 1

    def create_header_table(items_count):
        header_table = defaultdict(list)
        for item in items_count:
            if items_count[item] >= min_sup:
                header_table[item] = items_count[item]
        return header_table

    def create_conditional_database(transaction_patterns, header_table, items_count):
        cond_db = []
        for (transaction, pattern) in zip(transactions, transaction_patterns):
            cond_items = [item for item in transaction if item in header_table]
            if cond_items:
                cond_items.sort(key=lambda x: items_count[x], reverse=True)
                cond_db.append(cond_items)
        return cond_db

    def mine_patterns(header_table, freq_set, items_count):
        patterns = []
        for key in header_table:
            freq_set.add((key,))
            cond_header = {item: header_table[item] - header_table[key] for item in header_table if item != key}
            if cond_header:
                cond_items_count = {k: v for (k, v) in items_count.items() if k in cond_header}
                cond_items_patterns = mine_patterns(cond_header, freq_set, cond_items_count)
                for pattern in cond_items_patterns:
                    pattern = (key,) + pattern
                    patterns.append(pattern)
        return patterns
    items_count = Counter()
    scan_db(items_count)
    header_table = create_header_table(items_count)
    if not header_table:
        return []
    transactions = sorted(transactions, key=lambda x: sum((1 for i in x if i in header_table)))
    transaction_patterns = [[i for i in transaction if i in header_table] for transaction in transactions]
    patterns = set()
    for transaction_pattern in transaction_patterns:
        cond_db = create_conditional_database([transaction_pattern], header_table, items_count)
        cond_patterns = mine_patterns(header_table, patterns, items_count)
        patterns.update(cond_patterns)
    return sorted(patterns, key=lambda x: (-len(x), x))
