from collections import deque
def euclidean_distance(point1, point2):
    return np.linalg.norm(point1 - point2)
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    """Density-Based Spatial Clustering (DBSCAN).

    Parameters
    ----------
    data : np.ndarray
        A 2-D array with shape (n_samples, n_features).
    eps : float
        Radius that defines the neighbourhood of a point.
    min_samples : int
        Minimum number of points required to form a dense region (core point).

    Returns
    -------
    list[int]
        Cluster labels for every sample.  Noise points receive the label –1
        while clusters are numbered 1, 2, 3, … in the order they are found.
    """

    def build_neighborhood_list(data):
        n_samples = len(data)
        neighborhood_list = [set() for _ in range(n_samples)]
        for i in range(n_samples):
            for j in range(i + 1, n_samples):
                if euclidean_distance(data[i], data[j]) < eps:
                    neighborhood_list[i].add(j)
                    neighborhood_list[j].add(i)
        return neighborhood_list

    def bfs_explore_cluster(data, neighborhood_list, start_index, clusters):
        queue = deque([start_index])
        clusters[start_index] = current_cluster_id
        while queue:
            current_point = queue.popleft()
            for neighbor in neighborhood_list[current_point]:
                if clusters[neighbor] == 0:
                    clusters[neighbor] = current_cluster_id
                    queue.append(neighbor)
        return current_cluster_id
    n_samples = len(data)
    clusters = np.zeros(n_samples, dtype=int)
    current_cluster_id = 1
    neighborhood_list = build_neighborhood_list(data)
    for sample_index in range(n_samples):
        if clusters[sample_index] != 0:
            continue
        neighborhood = neighborhood_list[sample_index]
        if len(neighborhood) >= min_samples:
            clusters[sample_index] = current_cluster_id
            current_cluster_id = bfs_explore_cluster(data, neighborhood_list, sample_index, clusters)
        else:
            clusters[sample_index] = -1
    return clusters.tolist()

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    current_node = root
    while True:
        if task_type == 'classification':
            if current_node.y is not None:
                return current_node.y
            current_node = current_node.child[features[current_node.label]] != 0 and current_node.child[0] or current_node.child[1]
        elif task_type == 'regression':
            if current_node.y is not None:
                return current_node.y
            current_node = current_node.child[features[current_node.label] <= current_node.s]
        else:
            raise ValueError("Invalid task type. Choose between 'classification' and 'regression'.")

