import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    if epsilon == 0:
        return []
    (n_samples, n_features) = data.shape
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples

    def create_tree(data):
        sample = data[np.random.choice(data.shape[0], sample_size, replace=False)]
        height = math.ceil(math.log2(sample.shape[0]))
        if height == 0:
            return sample
        tree = {'split_feature': None, 'split_threshold': None, 'left': None, 'right': None}
        while height > 0:
            split_feature = np.random.randint(n_features)
            split_values = np.unique(sample[:, split_feature])
            if len(split_values) <= 1:
                break
            split_threshold = np.random.uniform(split_values[0], split_values[-1])
            left_indices = sample[:, split_feature] < split_threshold
            right_indices = ~left_indices
            if left_indices.sum() > 1:
                tree['left'] = create_tree(sample[left_indices])
            if right_indices.sum() > 1:
                tree['right'] = create_tree(sample[right_indices])
            if tree['left'] and tree['right']:
                break
            height -= 1
        return tree

    def path_length(tree, x):
        if not tree:
            return 0
        feature = tree['split_feature']
        threshold = tree['split_threshold']
        if x[feature] < threshold:
            if tree['left']:
                return 1 + path_length(tree['left'], x)
            else:
                n_left = len(tree['left'])
                return 1 + c(n_left)
        elif tree['right']:
            return 1 + path_length(tree['right'], x)
        else:
            n_right = len(tree['right'])
            return 1 + c(n_right)

    def c(n):
        if n == 1:
            return 0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n
    outliers_indices = []
    for i in range(n_trees):
        tree = create_tree(data)
        scores = [path_length(tree, x) for x in data]
        avg_path_length = sum(scores) / n_samples
        score_per_observation = [math.pow(2, -avg_path_length / phi) for avg_path_length in scores]
        epsilon_n = int(n_samples * epsilon)
        sorted_indices = sorted(range(n_samples), key=lambda k: score_per_observation[k])
        outliers_indices.extend(sorted_indices[:epsilon_n])
    unique_outliers_indices = sorted(set(outliers_indices))
    return unique_outliers_indices
