    for x in X_test:
        P_y_given_x = np.zeros(len(target_count))
        for (t, count) in target_count.items():
            P_y_given_x[t] = math.exp(np.dot(w.T, feature_label_count[t].values()))
            P_y_given_x[t] /= sum((math.exp(np.dot(w.T, feature_label_count[t].values())) for t in target_count))
        predicted_label = np.argmax(P_y_given_x)
        predictions.append(predicted_label)
    return predictions

