def calculate_matrix_mean(matrix: list[list[float]], mode: str) -> list[float]:
    if not matrix or not matrix[0]:
        raise ValueError('Matrix must not be empty and must have at least one row and one column.')
    if mode not in ['row', 'column']:
        raise ValueError("Mode must be either 'row' or 'column'.")
    if mode == 'row':
        means = [sum(row) / len(row) for row in matrix]
    elif mode == 'column':
        num_columns = len(matrix[0])
        means = [sum((matrix[row][col] for row in range(len(matrix)))) / len(matrix) for col in range(num_columns)]
    return means
