def determinant_4x4(matrix: list[list[int | float]]) -> float:

    def minor(matrix: list[list[int | float]], i: int, j: int) -> list[list[int | float]]:
        """Return the minor of the matrix after removing the i-th row and j-th column."""
        return [row[:j] + row[j + 1:] for row in matrix[:i] + matrix[i + 1:]]

    def determinant(matrix: list[list[int | float]]) -> float:
        """Recursively compute the determinant of a matrix."""
        if len(matrix) == 1:
            return matrix[0][0]
        elif len(matrix) == 2:
            return matrix[0][0] * matrix[1][1] - matrix[0][1] * matrix[1][0]
        else:
            det = 0
            for c in range(len(matrix)):
                det += (-1) ** c * matrix[0][c] * determinant(minor(matrix, 0, c))
            return det
    return determinant(matrix)
