from collections import Counter
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:

    def entropy(examples):
        if not examples:
            return 0
        label_counts = Counter((example[target_attr] for example in examples))
        total = len(examples)
        return -sum((count / total * math.log2(count / total) for count in label_counts.values()))

    def information_gain(examples, attribute):
        total_entropy = entropy(examples)
        value_counts = Counter((example[attribute] for example in examples))
        total = len(examples)
        weighted_entropy = 0
        for value in value_counts:
            subset = [example for example in examples if example[attribute] == value]
            weighted_entropy += len(subset) / total * entropy(subset)
        return total_entropy - weighted_entropy

    def majority_class(examples):
        label_counts = Counter((example[target_attr] for example in examples))
        return label_counts.most_common(1)[0][0]
    if not examples:
        return {}
    first_label = examples[0][target_attr]
    if all((example[target_attr] == first_label for example in examples)):
        return first_label
    if not attributes:
        return majority_class(examples)
    best_attribute = max(attributes, key=lambda attr: information_gain(examples, attr))
    tree = {best_attribute: {}}
    for value in set((example[best_attribute] for example in examples)):
        subset = [example for example in examples if example[best_attribute] == value]
        remaining_attributes = [attr for attr in attributes if attr != best_attribute]
        subtree = learn_decision_tree(subset, remaining_attributes, target_attr)
        tree[best_attribute][value] = subtree
    return tree
