def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    (n_samples, n_features) = data.shape
    alphas = np.zeros(n_samples)
    bias = 0.0

    def linear_kernel(x1, x2):
        return np.dot(x1, x2)

    def rbf_kernel(x1, x2, sigma=sigma):
        return np.exp(-np.linalg.norm(x1 - x2) ** 2 / (2 * sigma ** 2))
    if kernel == 'linear':
        kernel_func = linear_kernel
    elif kernel == 'rbf':
        kernel_func = rbf_kernel
    else:
        raise ValueError("Unsupported kernel type. Choose 'linear' or 'rbf'.")
    for t in range(1, iterations + 1):
        eta_t = 1 / (lambda_val * t)
        sum_alpha_y_k = np.zeros(n_samples)
        for i in range(n_samples):
            for j in range(n_samples):
                sum_alpha_y_k[i] += alphas[j] * labels[j] * kernel_func(data[i], data[j])
        for i in range(n_samples):
            if labels[i] * (sum_alpha_y_k[i] + bias) < 1:
                alphas[i] = (1 - eta_t * lambda_val) * alphas[i] + eta_t * labels[i]
            else:
                alphas[i] = (1 - eta_t * lambda_val) * alphas[i]
        sum_alpha_y = np.sum(alphas * labels)
        bias = bias + eta_t * sum_alpha_y
    alphas_rounded = np.round(alphas, 4)
    bias_rounded = np.round(bias, 4)
    return (alphas_rounded.tolist(), bias_rounded.tolist())
