import numpy as np
def sigmoid(x):
    return 1 / (1 + np.exp(-x))
def sigmoid_derivative(x):
    return x * (1 - x)
def mean_squared_error(y_true, y_pred):
    return np.mean((y_true - y_pred) ** 2)
def train_neuron(features: np.ndarray, labels: np.ndarray, initial_weights: np.ndarray, initial_bias: float, learning_rate: float, epochs: int) -> (np.ndarray, float, list[float]):
    weights = initial_weights.copy()
    bias = initial_bias
    mse_values = []
    for epoch in range(epochs):
        linear_combination = np.dot(features, weights) + bias
        predictions = sigmoid(linear_combination)
        mse = mean_squared_error(labels, predictions)
        mse_values.append(round(mse, 4))
        error = predictions - labels
        d_loss_d_pred = 2 * error / len(labels)
        d_pred_d_z = sigmoid_derivative(predictions)
        d_loss_d_z = d_loss_d_pred * d_pred_d_z
        d_loss_d_weights = np.dot(features.T, d_loss_d_z)
        d_loss_d_bias = np.sum(d_loss_d_z)
        weights -= learning_rate * d_loss_d_weights
        bias -= learning_rate * d_loss_d_bias
    return (weights.tolist(), round(bias, 4), mse_values)
