def compute_tf_idf(corpus, query):
    if not corpus:
        raise ValueError('The corpus is empty.')
    num_docs = len(corpus)
    df = defaultdict(int)
    for doc in corpus:
        unique_terms = set(doc)
        for term in unique_terms:
            df[term] += 1

    def term_frequency(term, doc):
        return doc.count(term) / len(doc) if doc else 0.0

    def inverse_document_frequency(term):
        return np.log((num_docs + 1) / (df[term] + 1)) + 1
    tf_idf_scores = []
    for doc in corpus:
        doc_scores = []
        for term in query:
            tf = term_frequency(term, doc)
            idf = inverse_document_frequency(term)
            tf_idf = tf * idf
            doc_scores.append(tf_idf)
        tf_idf_scores.append(doc_scores)
    tf_idf_scores = np.array(tf_idf_scores).round(4)
    return tf_idf_scores.tolist()
