def calculate_brightness(img):
    if not img or not img[0]:
        return -1
    row_length = len(img[0])
    for row in img:
        if len(row) != row_length:
            return -1
    total_pixel_value = 0
    pixel_count = 0
    for row in img:
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
            total_pixel_value += pixel
            pixel_count += 1
    average_brightness = total_pixel_value / pixel_count
    return round(average_brightness, 2)
