def performance_metrics(actual: List[int], predicted: List[int]) -> Tuple[List[List[int]], float, float, float, float]:
    if len(actual) != len(predicted):
        raise ValueError("The 'actual' and 'predicted' lists must have the same length.")
    if not all((x in [0, 1] for x in actual + predicted)):
        raise ValueError("All elements in 'actual' and 'predicted' must be either 0 or 1.")
    TP = FP = TN = FN = 0
    for (a, p) in zip(actual, predicted):
        if a == 1 and p == 1:
            TP += 1
        elif a == 0 and p == 0:
            TN += 1
        elif a == 0 and p == 1:
            FP += 1
        elif a == 1 and p == 0:
            FN += 1
    confusion_matrix = [[TN, FP], [FN, TP]]
    accuracy = (TP + TN) / (TP + TN + FP + FN) if TP + TN + FP + FN > 0 else 0.0
    precision = TP / (TP + FP) if TP + FP > 0 else 0.0
    recall = TP / (TP + FN) if TP + FN > 0 else 0.0
    f1_score = 2 * (precision * recall) / (precision + recall) if precision + recall > 0 else 0.0
    specificity = TN / (TN + FP) if TN + FP > 0 else 0.0
    negative_predictive_value = TN / (TN + FN) if TN + FN > 0 else 0.0
    return (confusion_matrix, round(accuracy, 3), round(f1_score, 3), round(specificity, 3), round(negative_predictive_value, 3))
