    for _ in range(n_tokens_to_generate):
        token_embeddings = wte[input_ids]
        positions = np.arange(len(input_ids))
        positional_embeddings = wpe[positions]
        x = token_embeddings + positional_embeddings
        q = k = v = x
        x = multi_head_attention(q, k, v, n_head)
        x = feed_forward(x, x.shape[-1], 20)
        x = layer_norm(x, ln_f_g, ln_f_b)
        logits = np.dot(x[-1], wte.T)
        next_token_id = np.argmax(logits)
        input_ids.append(next_token_id)
        input_ids = input_ids[-n_ctx:]
    generated_text = encoder.decode(input_ids)
    return generated_text
np.random.seed(42)
