import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity_link(mu):
        return mu

    def identity_inv_link(eta):
        return eta

    def identity_deriv(mu):
        return np.ones_like(mu)

    def log_link(mu):
        return np.log(mu)

    def log_inv_link(eta):
        return np.exp(eta)

    def log_deriv(mu):
        return 1.0 / mu

    def logit_link(mu):
        return np.log(mu / (1.0 - mu))

    def logit_inv_link(eta):
        return 1.0 / (1.0 + np.exp(-eta))

    def logit_deriv(mu):
        return 1.0 / (mu * (1.0 - mu))
    return {'identity': {'link': identity_link, 'inv_link': identity_inv_link, 'deriv': identity_deriv}, 'log': {'link': log_link, 'inv_link': log_inv_link, 'deriv': log_deriv}, 'logit': {'link': logit_link, 'inv_link': logit_inv_link, 'deriv': logit_deriv}}

