from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    transaction_list = [set(t) for t in transactions]
    N = len(transaction_list)
    if N == 0:
        return {}
    min_support = math.ceil(minsup * N)
    if min_support < 1:
        min_support = 1
    item_counter = Counter()
    for t in transaction_list:
        item_counter.update(t)
    freq_itemsets = {}
    L1 = []
    for (item, count) in item_counter.items():
        if count >= min_support:
            key = (item,)
            freq_itemsets[key] = count
            L1.append(key)
    if not L1:
        return {}
    Lk = L1
    k = 2
    while Lk:
        candidates = set()
        Lk_sorted = sorted(Lk)
        len_Lk = len(Lk_sorted)
        for i in range(len_Lk):
            for j in range(i + 1, len_Lk):
                l1 = Lk_sorted[i]
                l2 = Lk_sorted[j]
                if l1[:-1] == l2[:-1]:
                    candidate = tuple(sorted(set(l1) | set(l2)))
                    if len(candidate) == k:
                        candidates.add(candidate)
                else:
                    break
        if not candidates:
            break
        pruned_candidates = set()
        prev_freq_sets = set(Lk)
        for cand in candidates:
            all_subsets_frequent = True
            for subset in combinations(cand, k - 1):
                if tuple(sorted(subset)) not in prev_freq_sets:
                    all_subsets_frequent = False
                    break
            if all_subsets_frequent:
                pruned_candidates.add(cand)
        if not pruned_candidates:
            break
        cand_counter = Counter()
        for t in transaction_list:
            t_items = t
            for cand in pruned_candidates:
                if set(cand).issubset(t_items):
                    cand_counter[cand] += 1
        Lk = []
        for (cand, count) in cand_counter.items():
            if count >= min_support:
                key = tuple(sorted(cand))
                freq_itemsets[key] = count
                Lk.append(key)
        k += 1
    return freq_itemsets

import numpy as np
from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    The return value must be rounded to 4 decimal places.  If any required
    count is zero you should immediately return ``float('-inf')``.
    """
    if N < 1:
        raise ValueError('N must be >= 1')

    def ngrams(tokens, n):
        return [tuple(tokens[i:i + n]) for i in range(len(tokens) - n + 1)]
    if N == 1:
        unigram_counts = Counter(corpus)
        corpus_len = len(corpus)
    else:
        ngram_counts = Counter(ngrams(corpus, N))
        prefix_counts = Counter(ngrams(corpus, N - 1))
    total_logprob = 0.0
    num_ngrams = len(sequence) - N + 1
    if num_ngrams < 1:
        return 0.0
    for i in range(num_ngrams):
        ngram = tuple(sequence[i:i + N])
        if N == 1:
            count = unigram_counts.get(ngram[0], 0)
            if count == 0 or corpus_len == 0:
                return float('-inf')
            prob = count / corpus_len
        else:
            count = ngram_counts.get(ngram, 0)
            prefix = ngram[:-1]
            prefix_count = prefix_counts.get(prefix, 0)
            if count == 0 or prefix_count == 0:
                return float('-inf')
            prob = count / prefix_count
        if prob == 0.0:
            return float('-inf')
        total_logprob += np.log(prob)
    return round(total_logprob, 4)

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    if not transactions or min_support < 1:
        return []
    item_counter = Counter()
    for transaction in transactions:
        item_counter.update(transaction)
    frequent_items = {item for (item, count) in item_counter.items() if count >= min_support}
    if not frequent_items:
        return []

    def item_sort_key(item):
        return (-item_counter[item], item)
    frequent_items_sorted = sorted(frequent_items, key=item_sort_key)
    item_order = {item: i for (i, item) in enumerate(frequent_items_sorted)}

    def clean_transaction(transaction):
        return sorted([item for item in transaction if item in frequent_items], key=lambda x: item_order[x])
    cleaned_transactions = [clean_transaction(t) for t in transactions]
    cleaned_transactions = [t for t in cleaned_transactions if t]
    if not cleaned_transactions:
        return []

    class FPTreeNode:

        def __init__(self, item, parent):
            self.item = item
            self.count = 0
            self.parent = parent
            self.children = dict()
            self.link = None

        def increment(self, count):
            self.count += count

    class FPTree:

        def __init__(self):
            self.root = FPTreeNode(None, None)
            self.header_table = defaultdict(list)

        def add_transaction(self, transaction, count=1):
            node = self.root
            for item in transaction:
                if item not in node.children:
                    child = FPTreeNode(item, node)
                    node.children[item] = child
                    self.header_table[item].append(child)
                node = node.children[item]
                node.increment(count)

        def is_single_path(self):
            node = self.root
            while True:
                if len(node.children) == 0:
                    return True
                if len(node.children) > 1:
                    return False
                node = next(iter(node.children.values()))
    tree = FPTree()
    for transaction in cleaned_transactions:
        tree.add_transaction(transaction)

    def mine_tree(tree, suffix, min_support, item_order):
        """Recursively mine the FP-tree. Suffix is a tuple of items."""
        frequent_itemsets = []
        items = sorted(tree.header_table.keys(), key=lambda x: item_order[x], reverse=True)
        for item in items:
            new_itemset = tuple(sorted((item,) + suffix))
            support = sum((node.count for node in tree.header_table[item]))
            if support >= min_support:
                frequent_itemsets.append((new_itemset, support))
                conditional_pattern_base = []
                for node in tree.header_table[item]:
                    path = []
                    parent = node.parent
                    while parent and parent.item is not None:
                        path.append(parent.item)
                        parent = parent.parent
                    if path:
                        conditional_pattern_base.append((list(reversed(path)), node.count))
                conditional_tree = FPTree()
                cond_item_counter = Counter()
                for (path, count) in conditional_pattern_base:
                    cond_item_counter.update({i: count for i in path})
                cond_frequent_items = {i for (i, c) in cond_item_counter.items() if c >= min_support}
                if cond_frequent_items:

                    def cond_item_sort_key(i):
                        return (item_order[i],)
                    for (path, count) in conditional_pattern_base:
                        filtered_path = [i for i in path if i in cond_frequent_items]
                        if filtered_path:
                            filtered_path = sorted(filtered_path, key=lambda x: item_order[x])
                            conditional_tree.add_transaction(filtered_path, count)
                    frequent_itemsets.extend(mine_tree(conditional_tree, (item,) + suffix, min_support, item_order))
        return frequent_itemsets
    all_itemsets_with_support = mine_tree(tree, tuple(), min_support, item_order)
    result = [list(itemset) for (itemset, support) in all_itemsets_with_support]
    for itemset in result:
        itemset.sort()
    result.sort(key=lambda x: (len(x), x))
    return result

