import math
def escape_fire_maze(grid: list[str]) -> int:
    """Escape from a maze with spreading fire.

    Parameters
    ----------
    grid : list[str]
        Square maze represented as a list of strings. Each character must be
        '.', '#', or 'F'.  The agent starts at the upper-left corner (0,0) and
        wishes to reach the lower-right corner (n-1,n-1).  Fire starts in every
        cell marked with 'F' and spreads to the four neighbouring cells every
        minute.  The fire spreads first, then the agent moves.

    Returns
    -------
    int
        Minimum number of minutes required for the agent to reach the goal
        without entering a burning cell, or -1 if this is impossible.
    """
    n = len(grid)
    if n == 0:
        return -1
    if n == 1:
        if grid[0][0] == '.':
            return 0
        else:
            return -1
    dirs = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_time = [[math.inf for _ in range(n)] for _ in range(n)]
    fire_q = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                fire_q.append((i, j, 0))
            elif grid[i][j] == '#':
                fire_time[i][j] = -1
    while fire_q:
        (x, y, t) = fire_q.popleft()
        for (dx, dy) in dirs:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n:
                if grid[nx][ny] != '#' and fire_time[nx][ny] > t + 1:
                    fire_time[nx][ny] = t + 1
                    fire_q.append((nx, ny, t + 1))
    if fire_time[0][0] == 0 or fire_time[n - 1][n - 1] == 0:
        return -1
    if grid[0][0] == '#' or grid[n - 1][n - 1] == '#':
        return -1
    agent_q = deque()
    visited = [[math.inf for _ in range(n)] for _ in range(n)]
    agent_q.append((0, 0, 0))
    visited[0][0] = 0
    while agent_q:
        (x, y, t) = agent_q.popleft()
        if (x, y) == (n - 1, n - 1):
            return t
        for (dx, dy) in dirs:
            (nx, ny) = (x + dx, y + dy)
            nt = t + 1
            if 0 <= nx < n and 0 <= ny < n:
                if grid[nx][ny] == '#':
                    continue
                if fire_time[nx][ny] == -1 or fire_time[nx][ny] > nt:
                    if visited[nx][ny] > nt:
                        visited[nx][ny] = nt
                        agent_q.append((nx, ny, nt))
    return -1

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    class Node:
        __slots__ = ['is_leaf', 'class_label', 'feature', 'threshold', 'left', 'right']

        def __init__(self, is_leaf, class_label=None, feature=None, threshold=None, left=None, right=None):
            self.is_leaf = is_leaf
            self.class_label = class_label
            self.feature = feature
            self.threshold = threshold
            self.left = left
            self.right = right

    def gini(y):
        if len(y) == 0:
            return 0.0
        (_, counts) = np.unique(y, return_counts=True)
        probs = counts / counts.sum()
        return 1.0 - np.sum(probs ** 2)

    def majority_class(y):
        (vals, counts) = np.unique(y, return_counts=True)
        max_count = np.max(counts)
        majority = vals[counts == max_count]
        return int(np.min(majority))

    def best_split(X, y):
        (n_samples, n_features) = X.shape
        parent_gini = gini(y)
        best_gain = -1e-12
        best_feat = None
        best_thresh = None
        for feat in range(n_features):
            sorted_idx = np.argsort(X[:, feat], kind='mergesort')
            Xf = X[sorted_idx, feat]
            yf = y[sorted_idx]
            for i in range(1, n_samples):
                if Xf[i] == Xf[i - 1]:
                    continue
                if yf[i] == yf[i - 1]:
                    continue
                thresh = (Xf[i] + Xf[i - 1]) / 2
                left_mask = Xf <= thresh
                right_mask = Xf > thresh
                y_left = yf[left_mask]
                y_right = yf[right_mask]
                if len(y_left) == 0 or len(y_right) == 0:
                    continue
                gini_left = gini(y_left)
                gini_right = gini(y_right)
                n_left = len(y_left)
                n_right = len(y_right)
                weighted_gini = (n_left * gini_left + n_right * gini_right) / n_samples
                gain = parent_gini - weighted_gini
                if gain > best_gain or (np.isclose(gain, best_gain) and (feat < best_feat if best_feat is not None else True or (feat == best_feat and thresh < best_thresh))):
                    best_gain = gain
                    best_feat = feat
                    best_thresh = thresh
        return (best_feat, best_thresh, best_gain)

    def build_tree(X, y, depth):
        unique_labels = np.unique(y)
        if len(unique_labels) == 1:
            return Node(is_leaf=True, class_label=int(unique_labels[0]))
        if max_depth is not None and depth >= max_depth:
            return Node(is_leaf=True, class_label=majority_class(y))
        (feat, thresh, gain) = best_split(X, y)
        if feat is None or gain <= 1e-10:
            return Node(is_leaf=True, class_label=majority_class(y))
        left_mask = X[:, feat] <= thresh
        right_mask = X[:, feat] > thresh
        if not np.any(left_mask) or not np.any(right_mask):
            return Node(is_leaf=True, class_label=majority_class(y))
        left = build_tree(X[left_mask], y[left_mask], depth + 1)
        right = build_tree(X[right_mask], y[right_mask], depth + 1)
        return Node(is_leaf=False, feature=feat, threshold=thresh, left=left, right=right)

    def predict_one(x, node):
        while not node.is_leaf:
            if x[node.feature] <= node.threshold:
                node = node.left
            else:
                node = node.right
        return node.class_label
    tree = build_tree(X_train, y_train, depth=0)
    return [predict_one(x, tree) for x in X_test]

import numpy as np
def best_gini_split(X, y):
    """Find the best feature index and threshold that minimise the weighted Gini impurity.

    Parameters
    ----------
    X : list[list[float]] or numpy.ndarray
        A 2-D structure where each inner list/row contains the numerical
        feature values of one sample.
    y : list[int] or numpy.ndarray
        A 1-D structure containing the class labels corresponding to the rows
        of X.

    Returns
    -------
    tuple
        A tuple `(best_feature_index, best_threshold_value, best_gini)` where
        `best_gini` is rounded to 4 decimal places.  If no valid split exists
        the function must return `(-1, None, round(G_whole_dataset, 4))`.
    """
    X = np.asarray(X)
    y = np.asarray(y)
    (n_samples, n_features) = X.shape

    def gini(labels):
        if len(labels) == 0:
            return 0.0
        (_, counts) = np.unique(labels, return_counts=True)
        probs = counts / counts.sum()
        return 1.0 - np.sum(probs ** 2)
    G_whole = gini(y)
    best = {'gini': np.inf, 'feature': -1, 'threshold': None}
    if len(np.unique(y)) == 1:
        return (-1, None, round(G_whole, 4))
    for j in range(n_features):
        X_col = X[:, j]
        unique_vals = np.unique(X_col)
        for v in unique_vals:
            left_mask = X_col <= v
            right_mask = X_col > v
            n_left = np.sum(left_mask)
            n_right = np.sum(right_mask)
            if n_left == 0 or n_right == 0:
                continue
            y_left = y[left_mask]
            y_right = y[right_mask]
            G_left = gini(y_left)
            G_right = gini(y_right)
            G_split = n_left / n_samples * G_left + n_right / n_samples * G_right
            update = False
            if G_split < best['gini'] - 1e-12:
                update = True
            elif abs(G_split - best['gini']) < 1e-12:
                if j < best['feature']:
                    update = True
                elif j == best['feature']:
                    if v < best['threshold']:
                        update = True
            if update:
                best['gini'] = G_split
                best['feature'] = j
                best['threshold'] = v
    if best['feature'] == -1:
        return (-1, None, round(G_whole, 4))
    else:
        return (best['feature'], best['threshold'], round(best['gini'], 4))

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported = {'euclidean', 'manhattan', 'chebyshev', 'cosine'}
    if name not in supported:

        def invalid(*args, **kwargs):
            return -1
        return invalid

    def validate_and_convert(vec):
        if not isinstance(vec, (list, tuple, np.ndarray)):
            return None
        try:
            arr = np.asarray(vec, dtype=float)
        except Exception:
            return None
        if arr.ndim != 1 or arr.size == 0:
            return None
        return arr

    def d(x, y):
        x_arr = validate_and_convert(x)
        y_arr = validate_and_convert(y)
        if x_arr is None or y_arr is None:
            return -1
        if x_arr.shape != y_arr.shape:
            return -1
        if name == 'euclidean':
            dist = np.linalg.norm(x_arr - y_arr)
        elif name == 'manhattan':
            dist = np.sum(np.abs(x_arr - y_arr))
        elif name == 'chebyshev':
            dist = np.max(np.abs(x_arr - y_arr))
        elif name == 'cosine':
            norm_x = np.linalg.norm(x_arr)
            norm_y = np.linalg.norm(y_arr)
            if norm_x == 0 or norm_y == 0:
                return -1
            cosine_sim = np.dot(x_arr, y_arr) / (norm_x * norm_y)
            dist = 1 - cosine_sim
        else:
            return -1
        return round(float(dist), 4)
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def hand_value(hand: list[int]) -> int:
        """Compute the best score for a hand (max ≤ 21, with usable ace if possible)."""
        total = sum(hand)
        if 1 in hand and total + 10 <= 21:
            return total + 10
        return total

    def is_natural_blackjack(hand: list[int]) -> bool:
        """Check if the hand is a natural blackjack (two cards: Ace + 10)."""
        return len(hand) == 2 and 1 in hand and (10 in hand)
    player_score = hand_value(player)
    dealer_score = hand_value(dealer)
    player_bust = player_score > 21
    dealer_bust = dealer_score > 21
    player_natural = is_natural_blackjack(player)
    if player_bust:
        return -1.0
    if dealer_bust:
        return 1.0
    if player_score > dealer_score:
        if player_natural:
            return 1.5
        return 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
        return 0.0

import math
from collections import Counter
def best_split(feature, target):
    """Determine the numerical threshold that produces the highest information gain.

    Parameters
    ----------
    feature : list[int | float] | 1-D numpy.ndarray
        Numerical values of a single attribute.
    target  : list[int] | 1-D numpy.ndarray
        Corresponding class labels.

    Returns
    -------
    tuple
        (threshold, information_gain) where
        * threshold – float rounded to 4 decimals or None when no useful split exists;
        * information_gain – float rounded to 4 decimals.
    """
    try:
        feature = feature.tolist()
    except AttributeError:
        pass
    try:
        target = target.tolist()
    except AttributeError:
        pass
    n = len(feature)
    if n == 0:
        return (None, 0.0)
    sorted_data = sorted(zip(feature, target), key=lambda x: x[0])
    sorted_feature = [x[0] for x in sorted_data]
    sorted_target = [x[1] for x in sorted_data]
    thresholds = []
    for i in range(1, n):
        if sorted_feature[i] != sorted_feature[i - 1]:
            t = (sorted_feature[i] + sorted_feature[i - 1]) / 2.0
            thresholds.append(t)
    if not thresholds:
        return (None, 0.0)

    def entropy(labels):
        total = len(labels)
        if total == 0:
            return 0.0
        counts = Counter(labels)
        ent = 0.0
        for c in counts.values():
            p = c / total
            if p > 0:
                ent -= p * math.log2(p)
        return ent
    parent_entropy = entropy(sorted_target)
    if parent_entropy == 0.0:
        return (None, 0.0)
    best_ig = -1
    best_threshold = None
    for t in thresholds:
        left = 0
        right = n
        while left < right:
            mid = (left + right) // 2
            if sorted_feature[mid] < t:
                left = mid + 1
            else:
                right = mid
        split_idx = left
        left_labels = sorted_target[:split_idx]
        right_labels = sorted_target[split_idx:]
        nL = len(left_labels)
        nR = len(right_labels)
        if nL == 0 or nR == 0:
            continue
        hL = entropy(left_labels)
        hR = entropy(right_labels)
        ig = parent_entropy - nL / n * hL - nR / n * hR
        if ig > best_ig + 1e-12:
            best_ig = ig
            best_threshold = t
        elif abs(ig - best_ig) < 1e-12 and t < best_threshold:
            best_threshold = t
    if best_ig <= 0 or best_threshold is None:
        return (None, 0.0)
    else:
        return (round(best_threshold, 4), round(best_ig, 4))

from typing import Any, Dict, List, Tuple
def to_networkx(G: Dict[str, Any]) -> Dict[Any, List[Tuple[Any, float]]]:
    """Convert a custom graph dictionary into a plain adjacency dictionary.

    Parameters
    ----------
    G : dict
        A graph represented as a dictionary with the keys:
          * 'is_directed' (bool)
          * '_V2I'        (dict mapping vertex label -> index)
          * '_G'          (list of adjacency lists, each edge is a
                           tuple (source_label, target_label, weight))

    Returns
    -------
    dict
        A dictionary where each key is a vertex label and the value is a list
        of `(neighbour, weight)` tuples.  For undirected graphs every edge
        appears once in each direction and the neighbour lists are sorted
        alphabetically.
    """
    if not G or '_V2I' not in G or (not G['_V2I']):
        return {}
    is_directed = G['is_directed']
    v2i = G['_V2I']
    i2v = [None] * len(v2i)
    for (v, i) in v2i.items():
        i2v[i] = v
    adj: Dict[Any, List[Tuple[Any, float]]] = {v: [] for v in v2i}
    if is_directed:
        for (i, edges) in enumerate(G['_G']):
            u = i2v[i]
            for (src, tgt, w) in edges:
                if src == u:
                    adj[u].append((tgt, w))
        for u in adj:
            adj[u].sort(key=lambda x: x[0])
    else:
        seen = set()
        for (i, edges) in enumerate(G['_G']):
            u = i2v[i]
            for (src, tgt, w) in edges:
                (a, b) = (min(src, tgt), max(src, tgt))
                key = (a, b, w)
                if key not in seen:
                    seen.add(key)
                    adj[src].append((tgt, w))
                    if src != tgt:
                        adj[tgt].append((src, w))
        for u in adj:
            adj[u].sort(key=lambda x: x[0])
    return adj

import math
from collections import Counter, defaultdict
from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model.

    Parameters
    ----------
    corpus : list[str]
        A list of training sentences. Each sentence is a plain string; tokens
        are assumed to be separated by white-spaces.
    query : str
        The sentence whose probability should be calculated.
    N : int
        The order of the N-gram model (N ≥ 1).

    Returns
    -------
    float
        The sum of natural logarithms of the probabilities of every
        consecutive N-gram occurring in *query*, rounded to 4 decimal places.
        If any necessary N-gram is unseen in the corpus the function returns
        float('-inf').
    """
    if N < 1:
        raise ValueError('N must be at least 1')

    def pad_and_tokenize(sentence: str, N: int) -> List[str]:
        tokens = sentence.strip().split()
        if N == 1:
            return tokens
        else:
            return ['<bol>'] * (N - 1) + tokens + ['<eol>']
    ngram_counts = Counter()
    prefix_counts = Counter()
    unigram_count = Counter()
    total_unigrams = 0
    for sent in corpus:
        tokens = pad_and_tokenize(sent, N)
        if N == 1:
            unigram_count.update(tokens)
            total_unigrams += len(tokens)
        else:
            for i in range(len(tokens) - N + 1):
                ngram = tuple(tokens[i:i + N])
                ngram_counts[ngram] += 1
                prefix = tuple(tokens[i:i + N - 1])
                prefix_counts[prefix] += 1
    if N == 1:
        query_tokens = pad_and_tokenize(query, N)
        if not query_tokens:
            return float('-inf')
        logprob = 0.0
        for token in query_tokens:
            count = unigram_count[token]
            if count == 0 or total_unigrams == 0:
                return float('-inf')
            prob = count / total_unigrams
            logprob += math.log(prob)
        return round(logprob, 4)
    query_tokens = pad_and_tokenize(query, N)
    if len(query_tokens) < N:
        return float('-inf')
    logprob = 0.0
    for i in range(len(query_tokens) - N + 1):
        ngram = tuple(query_tokens[i:i + N])
        prefix = tuple(query_tokens[i:i + N - 1])
        ngram_count = ngram_counts[ngram]
        prefix_count = prefix_counts[prefix]
        if ngram_count == 0 or prefix_count == 0:
            return float('-inf')
        prob = ngram_count / prefix_count
        logprob += math.log(prob)
    return round(logprob, 4)

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries.

    Parameters
    ----------
    transactions : list[list[str]]
        A list where each element is a list of items representing one transaction.
    min_support : int
        Minimum number of transactions an item has to appear in to be kept.

    Returns
    -------
    dict
        The root node of the FP-Tree.  Each node contains two keys:
        "support" and "children" (the latter mapping item → child-node).
    """
    item_counter = Counter()
    for transaction in transactions:
        unique_items = set(transaction)
        item_counter.update(unique_items)
    frequent_items = {item for (item, count) in item_counter.items() if count >= min_support}
    if not frequent_items:
        return {'support': 0, 'children': {}}
    sorted_items = sorted(frequent_items, key=lambda item: (-item_counter[item], item))
    item_order = {item: idx for (idx, item) in enumerate(sorted_items)}
    root = {'support': 0, 'children': {}}
    for transaction in transactions:
        filtered = [item for item in set(transaction) if item in frequent_items]
        if not filtered:
            continue
        ordered = sorted(filtered, key=lambda item: item_order[item])
        node = root
        node['support'] += 1
        for item in ordered:
            children = node['children']
            if item not in children:
                children[item] = {'support': 0, 'children': {}}
            node = children[item]
            node['support'] += 1
    return root

import numpy as np
def linear_kernel(X: list[list[int | float]] | 'np.ndarray', Y: list[list[int | float]] | 'np.ndarray' | None=None, c0: int | float=0) -> list[list[float]] | int:
    """Return the linear kernel matrix k(x, y) = x·y + c0.

    Parameters
    ----------
    X : 2-D list or NumPy array with shape (N, C)
        Collection of N input vectors.
    Y : 2-D list or NumPy array with shape (M, C) or None, optional
        Collection of M input vectors.  If None, the function must set
        ``Y = X``.  Default is None.
    c0 : int or float, optional
        Additive constant of the kernel.  Default is 0.

    Returns
    -------
    list[list[float]] | int
        The (N, M) kernel matrix where entry (i, j) equals
        X[i]·Y[j] + c0, rounded to 4 decimals.  Return -1 when X and Y have
        incompatible shapes (different number of columns).
    """
    X_arr = np.array(X, dtype=np.float64)
    if X_arr.ndim != 2:
        if X_arr.size == 0:
            return []
        else:
            return -1
    (N, C) = X_arr.shape
    if Y is None:
        Y_arr = X_arr
    else:
        Y_arr = np.array(Y, dtype=np.float64)
        if Y_arr.ndim != 2:
            if Y_arr.size == 0:
                if C == 0:
                    return []
                else:
                    return -1
            else:
                return -1
    if X_arr.shape[1] != Y_arr.shape[1]:
        return -1
    if N == 0:
        return []
    K = X_arr @ Y_arr.T + c0
    K_rounded = np.round(K, 4)
    return K_rounded.tolist()

from typing import Any, Dict as TypingDict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: TypingDict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def is_composite_continuous(space: Space) -> bool:
        if isinstance(space, Tuple):
            return all((isinstance(sub, Box) for sub in space.spaces))
        elif isinstance(space, Dict):
            return all((isinstance(sub, Box) for sub in space.spaces.values()))
        else:
            return False

    def check(space: Space, is_tuple: bool) -> bool:
        if is_tuple:
            return is_composite_continuous(space)
        else:
            return isinstance(space, Box)
    cont_action = check(env.action_space, tuple_action)
    cont_obs = check(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor."""

    class TreeNode:

        def __init__(self, feature=None, threshold=None, left=None, right=None, value=None):
            self.feature = feature
            self.threshold = threshold
            self.left = left
            self.right = right
            self.value = value

        def is_leaf(self):
            return self.value is not None

    class RegressionTree:

        def __init__(self, max_depth=3, min_samples_split=2):
            self.max_depth = max_depth
            self.min_samples_split = min_samples_split
            self.root = None

        def fit(self, X, y):
            self.root = self._build(X, y, depth=0)

        def _build(self, X, y, depth):
            (n_samples, n_features) = X.shape
            if depth >= self.max_depth or n_samples < self.min_samples_split or np.all(y == y[0]):
                return TreeNode(value=np.mean(y))
            (best_feat, best_thresh, best_loss, best_left_idx, best_right_idx) = (None, None, np.inf, None, None)
            for feat in range(n_features):
                X_col = X[:, feat]
                sorted_idx = np.argsort(X_col)
                X_col_sorted = X_col[sorted_idx]
                y_sorted = y[sorted_idx]
                for i in range(1, n_samples):
                    if X_col_sorted[i] == X_col_sorted[i - 1]:
                        continue
                    thresh = (X_col_sorted[i] + X_col_sorted[i - 1]) / 2
                    left_mask = X_col <= thresh
                    right_mask = X_col > thresh
                    if np.sum(left_mask) == 0 or np.sum(right_mask) == 0:
                        continue
                    y_left = y[left_mask]
                    y_right = y[right_mask]
                    loss = np.var(y_left) * len(y_left) + np.var(y_right) * len(y_right)
                    if loss < best_loss:
                        best_feat = feat
                        best_thresh = thresh
                        best_loss = loss
                        best_left_idx = left_mask
                        best_right_idx = right_mask
            if best_feat is None:
                return TreeNode(value=np.mean(y))
            parent_var = np.var(y) * len(y)
            if best_loss >= parent_var - 1e-10:
                return TreeNode(value=np.mean(y))
            left = self._build(X[best_left_idx], y[best_left_idx], depth + 1)
            right = self._build(X[best_right_idx], y[best_right_idx], depth + 1)
            return TreeNode(feature=best_feat, threshold=best_thresh, left=left, right=right)

        def _predict_row(self, x, node):
            while not node.is_leaf():
                if x[node.feature] <= node.threshold:
                    node = node.left
                else:
                    node = node.right
            return node.value

        def predict(self, X):
            return np.array([self._predict_row(x, self.root) for x in X])
    n_train = X_train.shape[0]
    y_pred = np.full(n_train, np.mean(y_train))
    trees = []
    for est in range(n_estimators):
        residuals = y_train - y_pred
        tree = RegressionTree(max_depth=max_depth)
        tree.fit(X_train, residuals)
        update = tree.predict(X_train)
        y_pred += learning_rate * update
        trees.append(tree)
    y_test_pred = np.full(X_test.shape[0], np.mean(y_train))
    for tree in trees:
        y_test_pred += learning_rate * tree.predict(X_test)
    return [round(float(x), 4) for x in y_test_pred]

import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function must return the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """

    def add_intercept(A):
        return np.hstack([np.ones((A.shape[0], 1)), A])
    if fit_intercept:
        if X.ndim == 1:
            X = X.reshape(-1, 1)
        if X_new.ndim == 1:
            X_new = X_new.reshape(-1, 1)
        X = add_intercept(X)
        X_new = add_intercept(X_new)
    (N, M) = X.shape
    if np.isscalar(mu):
        mu_vec = np.full(M, mu, dtype=float)
    else:
        mu = np.asarray(mu, dtype=float)
        if mu.ndim == 0:
            mu_vec = np.full(M, mu.item(), dtype=float)
        elif mu.shape == (M,):
            mu_vec = mu
        else:
            raise ValueError('mu must be a scalar or a vector of length matching the number of features (with intercept if fitted)')
    if V is None:
        V_mat = np.eye(M)
    elif np.isscalar(V):
        V_mat = np.eye(M) * float(V)
    else:
        V = np.asarray(V, dtype=float)
        if V.ndim == 1:
            if V.shape[0] != M:
                raise ValueError('V diagonal length does not match number of features')
            V_mat = np.diag(V)
        elif V.shape == (M, M):
            V_mat = V
        else:
            raise ValueError('V must be a scalar, a vector of length M, or a (M,M) matrix')
    try:
        V_inv = np.linalg.inv(V_mat)
    except np.linalg.LinAlgError:
        raise ValueError('Prior covariance V is not invertible')
    XT_X = X.T @ X
    Sigma_p_inv = V_inv + XT_X
    try:
        Sigma_p = np.linalg.inv(Sigma_p_inv)
    except np.linalg.LinAlgError:
        Sigma_p = np.linalg.pinv(Sigma_p_inv)
    XT_y = X.T @ y
    mu_p = Sigma_p @ (V_inv @ mu_vec + XT_y)
    y_pred = X_new @ mu_p
    return [round(float(val), 4) for val in y_pred]

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Compute the Root Mean Squared Logarithmic Error (RMSLE) between two lists."""
    if len(actual) != len(predicted):
        return -1
    for (a, p) in zip(actual, predicted):
        if not isinstance(a, (int, float)) or not isinstance(p, (int, float)):
            return -1
        if a < 0 or p < 0:
            return -1
    actual_arr = np.array(actual, dtype=np.float64)
    predicted_arr = np.array(predicted, dtype=np.float64)
    log_actual = np.log1p(actual_arr)
    log_predicted = np.log1p(predicted_arr)
    squared_diff = (log_predicted - log_actual) ** 2
    mean_squared_log_error = np.mean(squared_diff)
    rmsle = np.sqrt(mean_squared_log_error)
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in (0, 1, 2):
        return -1
    is_scalar = np.isscalar(x)
    arr = np.array(x, dtype=np.float64) if not is_scalar else float(x)

    def _sigmoid(z):
        return 1.0 / (1.0 + np.exp(-z))
    if is_scalar:
        s = _sigmoid(arr)
        if order == 0:
            result = s
        elif order == 1:
            result = s * (1 - s)
        else:
            result = s * (1 - s) * (1 - 2 * s)
        return round(result, 4)
    else:
        s = _sigmoid(arr)
        if order == 0:
            result = s
        elif order == 1:
            result = s * (1 - s)
        else:
            result = s * (1 - s) * (1 - 2 * s)
        return np.round(result, 4).tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    if not isinstance(X, list) or len(X) == 0:
        return -1
    if not all((isinstance(row, list) and len(row) == len(X[0]) for row in X)):
        return -1
    N = len(X)
    C = len(X[0])
    if C == 0:
        return -1
    if Y is None:
        Y = X
    else:
        if not isinstance(Y, list) or len(Y) == 0:
            return -1
        if not all((isinstance(row, list) and len(row) == len(Y[0]) for row in Y)):
            return -1
        M = len(Y)
        C2 = len(Y[0])
        if C2 != C:
            return -1
    M = len(Y)
    if sigma is None:
        sigma_arr = np.full(C, np.sqrt(C / 2))
    elif isinstance(sigma, (int, float)):
        if sigma <= 0:
            return -1
        sigma_arr = np.full(C, float(sigma))
    elif isinstance(sigma, (list, np.ndarray)):
        if len(sigma) != C:
            return -1
        try:
            sigma_arr = np.array(sigma, dtype=float)
        except Exception:
            return -1
        if np.any(sigma_arr <= 0):
            return -1
    else:
        return -1
    try:
        X_arr = np.array(X, dtype=float)
        Y_arr = np.array(Y, dtype=float)
    except Exception:
        return -1
    diff = (X_arr[:, None, :] - Y_arr[None, :, :]) / sigma_arr
    sq_dist = np.sum(diff ** 2, axis=2)
    K = np.exp(-0.5 * sq_dist)
    K_rounded = np.round(K, 4).tolist()
    return K_rounded

import numpy as np
def to_categorical(labels: list | tuple | 'np.ndarray', num_classes: int | None=None) -> list[list[int]]:
    """Convert integer class labels to one-hot encoded format.

    Args:
        labels: 1-D sequence of non-negative integer labels.
        num_classes: Total number of classes. If *None*, infer as
            ``max(labels) + 1``.

    Returns:
        A list of lists representing the one-hot encoded labels, or -1
        when the input is invalid (negative label or `num_classes` too
        small).
    """
    if isinstance(labels, np.ndarray):
        labels_list = labels.tolist()
    else:
        labels_list = list(labels)
    if len(labels_list) == 0:
        if num_classes is None:
            return []
        else:
            return []
    for label in labels_list:
        if not isinstance(label, (int, np.integer)) or label < 0:
            return -1
    max_label = max(labels_list)
    inferred_classes = max_label + 1
    if num_classes is None:
        num_classes = inferred_classes
    elif num_classes < inferred_classes:
        return -1
    result = []
    for label in labels_list:
        row = [0] * num_classes
        row[label] = 1
        result.append(row)
    return result

