    item_counter = Counter()
    for transaction in transactions:
        item_counter.update(transaction)
    freq_items = {item for (item, cnt) in item_counter.items() if cnt >= min_sup}
    if not freq_items:
        return (None, None)

    def sort_items(transaction):
        items = [item for item in transaction if item in freq_items]
        items.sort(key=lambda x: (-item_counter[x], x))
        return items
    header_table = {}
    for item in freq_items:
        header_table[item] = [item_counter[item], None]
    root = FPTreeNode(None, 0, None)
    for transaction in transactions:
        ordered_items = sort_items(transaction)
        current_node = root
        for item in ordered_items:
            if item in current_node.children:
                child = current_node.children[item]
                child.increment(1)
            else:
                child = FPTreeNode(item, 1, current_node)
                current_node.children[item] = child
                if header_table[item][1] is None:
                    header_table[item][1] = child
                else:
                    node = header_table[item][1]
                    while node.link is not None:
                        node = node.link
                    node.link = child
            current_node = child
    return (root, header_table)
