            actual_proc = actual.argmax(axis=1)
        else:
            actual_proc = actual
        if isinstance(predicted, np.ndarray) and predicted.ndim == 2 and (predicted.shape[1] > 1):
            predicted_proc = predicted.argmax(axis=1)
        else:
            predicted_proc = predicted
        return function(actual_proc, predicted_proc)
    return wrapper

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def grad_scalar(x):
        return float(np.round(x * (1 - x), 4))
    if isinstance(z, (int, float, np.generic)):
        return grad_scalar(z)
    if isinstance(z, list) and len(z) == 0:
        return []
    if isinstance(z, np.ndarray):
        grad = np.round(z * (1 - z), 4)
        return grad.tolist()
    if isinstance(z, list):
        return [sigmoid_grad(item) for item in z]
    raise TypeError('Input must be a scalar, list, or numpy array.')

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    node = root
    while True:
        if hasattr(node, 'y') and node.y is not None:
            return node.y
        if task_type == 'classification':
            feature_value = features[node.label]
            for child in node.child:
                if child.x == feature_value:
                    node = child
                    break
            else:
                node = node.child[1]
        elif task_type == 'regression':
            feature_value = features[node.label]
            if feature_value <= node.s:
                node = node.child[0]
            else:
                node = node.child[1]
        else:
            raise ValueError("Unknown task_type: must be 'classification' or 'regression'")

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if epsilon == 0:
        return []

    def c(n):
        if n <= 1:
            return 0.0
        else:
            return 2.0 * np.log(n - 1) + 0.5772156649 - 2.0 * (n - 1) / n
    if n_samples > 1:
        phi = 2.0 * np.log(n_samples - 1) - 2.0 * (n_samples - 1) / n_samples
    else:
        phi = 1.0
    height_limit = int(math.ceil(math.log2(sample_size)))

    class Node:
        __slots__ = ['feature', 'threshold', 'left', 'right', 'size', 'is_leaf']

        def __init__(self, feature=None, threshold=None, left=None, right=None, size=None, is_leaf=False):
            self.feature = feature
            self.threshold = threshold
            self.left = left
            self.right = right
            self.size = size
            self.is_leaf = is_leaf

    def build_tree(X, current_depth, height_limit):
        (n, m) = X.shape
        if current_depth >= height_limit or n <= 1:
            return Node(size=n, is_leaf=True)
        feature = np.random.randint(m)
        min_val = X[:, feature].min()
        max_val = X[:, feature].max()
        if min_val == max_val:
            return Node(size=n, is_leaf=True)
        threshold = np.random.uniform(min_val, max_val)
        left_mask = X[:, feature] < threshold
        right_mask = ~left_mask
        if left_mask.sum() == 0 or right_mask.sum() == 0:
            return Node(size=n, is_leaf=True)
        left = build_tree(X[left_mask], current_depth + 1, height_limit)
        right = build_tree(X[right_mask], current_depth + 1, height_limit)
        return Node(feature=feature, threshold=threshold, left=left, right=right, is_leaf=False)

    def path_length(x, node, current_depth):
        if node.is_leaf:
            return current_depth + c(node.size)
        if x[node.feature] < node.threshold:
            return path_length(x, node.left, current_depth + 1)
        else:
            return path_length(x, node.right, current_depth + 1)
    trees = []
    subsample_indices = []
    for _ in range(n_trees):
        if sample_size >= n_samples:
            idx = np.arange(n_samples)
        else:
            idx = np.random.choice(n_samples, size=sample_size, replace=False)
        X_sub = data[idx]
        tree = build_tree(X_sub, 0, height_limit)
        trees.append(tree)
        subsample_indices.append(idx)
    path_lengths = np.zeros((n_samples, n_trees), dtype=float)
    for (t, (tree, idx)) in enumerate(zip(trees, subsample_indices)):
        X_sub = data[idx]
        for i in range(n_samples):
            x = data[i]
            path_lengths[i, t] = path_length(x, tree, 0)
    avg_path_length = path_lengths.mean(axis=1)
    scores = np.power(2, -avg_path_length / phi)
    n_outliers = int(np.floor(epsilon * n_samples))
    if n_outliers == 0:
        return []
    outlier_indices = np.argpartition(-scores, n_outliers - 1)[:n_outliers]
    outlier_indices_sorted = np.sort(outlier_indices)
    return outlier_indices_sorted.tolist()
